import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (flowFile == null) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def slurper = new JsonSlurper()
    def content = slurper.parse(inputStream)

    // Calcul VPD (Vapor Pressure Deficit) selon formule Tetens
    def temp = content.temp_air ?: content.air_temp_internal ?: 25.0
    def rh = content.humidity ?: content.air_humidity ?: 60.0

    // Pression de vapeur saturante (es) en kPa
    def es = 0.6108 * Math.exp((17.27 * temp) / (temp + 237.3))
    // Pression de vapeur actuelle (ea)
    def ea = es * (rh / 100.0)
    // VPD = es - ea
    def vpd = es - ea

    content.vpd = Math.round(vpd * 1000) / 1000.0
    content.vpd_status = vpd < 0.8 ? "LOW" : (vpd > 1.2 ? "HIGH" : "OPTIMAL")

    outputStream.write(JsonOutput.toJson(content).getBytes("UTF-8"))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
